package com.ukhurshed.movie

import android.content.Intent
import android.net.Uri
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import android.widget.TextView
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import kotlinx.android.synthetic.main.actors_recycler.*

class MainActivity : AppCompatActivity() {



    private lateinit var itemAdapter: ActorListAdapter
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.actors_recycler)


        recycler.apply {
            layoutManager = LinearLayoutManager(this@MainActivity)
            itemAdapter = ActorListAdapter()
            val data = generateActors()
            itemAdapter.submitList(data)
            adapter = itemAdapter

        }

    }

    private fun generateActors():List<Actor>{
        return listOf(
            Actor("Emma Stone","https://img5.goodfon.ru/wallpaper/nbig/8/36/vzgliad-portret-makiiazh-aktrisa-emma-stone-emma-stoun-hair.jpg", true),
            Actor("Anne Hathway", "data:image/jpeg;base64,",true)
        )
    }
}
